mapper.select:
select COUNT(1) from activity,club
,club_admin
where 1=1 AND club_union_id = club.id AND `type` = 2
AND audit_states = #{status}
AND user_id = #{userId} AND club.id = club_admin.club_id
limit #{startRecord}, #{endRecord}

mapper.insert:
INSERT INTO `activity` (
club_union_id, `type`, theme, introduction, content, member_activity, time_begin, time_end, enroll_deadline, audit_states, money, create_time, update_time,
) VALUES (
#{clubUnionId}, #{type}, #{theme}, #{introduction}, #{content}, #{memberActivity}, #{timeBegin}, #{timeEnd}, #{enrollDeadline}, #{auditStates}, #{money}, #{createTime}, #{updateTime},
)

mapper.select:
select
from activity,`union`
,activity_enroll activity_enroll
where 1=1 AND club_union_id = `union`.id AND `type` = 1 AND audit_states = 4
AND activity.member_activity = #{memberActivity}
AND activity_enroll.activity_id = activity.id AND activity_enroll.student_id = #{studentId}
UNION ALL select
FROM activity, club
,activity_enroll activity_enroll
WHERE 1 = 1 AND club_union_id = club.id AND `type` = 2 AND audit_states = 4
AND activity.member_activity = #{memberActivity}
AND activity_enroll.activity_id = activity.id AND activity_enroll.student_id = #{studentId}
limit #{startRecord}, #{endRecord}
