mapper.select:
select
from activity,club where 1=1 AND club_union_id = club.id
AND auditStates = #{status}
limit #{startRecord}, #{endRecord}

mapper.insert:
INSERT INTO `activity` (
club_union_id, `type`, theme, introduction, content, member_activity, `time`, enroll_deadline, audit_states, create_time, update_time,
) VALUES (
#{clubUnionId}, #{type}, #{theme}, #{introduction}, #{content}, #{memberActivity}, #{time}, #{enrollDeadline}, #{auditStates}, #{createTime}, #{updateTime},
)
